import { useEffect, useState } from "react";
import { fetchAllTariffs, addTariff } from "../requests";

import TariffCard from "./ui/TariffCard";
import AddTariff from "./forms/AddTariff";
import AddServiceType from "./forms/AddServiceType"; // Импорт компонента AddServiceType

export default function Main() {
    const [data, setData] = useState([]); // Состояние для тарифов
    const [shown, setShown] = useState(false); // Состояние для отображения формы "Добавить тариф"
    const [shownServiceType, setShownServiceType] = useState(false); // Состояние для отображения формы "Добавить тип услуги"

    // Загрузка тарифов
    useEffect(() => {
        const loadTariffs = async () => {
            try {
                const tariffs = await fetchAllTariffs(); // Получаем данные о тарифах
                setData(tariffs || []); // Устанавливаем данные в состояние
            } catch (error) {
                console.error("Ошибка при загрузке тарифов:", error);
                setData([]); // Устанавливаем пустой массив в случае ошибки
            }
        };

        loadTariffs(); // Вызываем функцию загрузки тарифов
    }, []);

    return (
        <>
            <div className="main-block-menu">
                {!shown && (
                    <button
                        className="filed-button"
                        onClick={() => {
                            setShown(true);
                            window.scrollTo(0, 0);
                        }}
                    >
                        Добавить тариф
                    </button>
                )}
                {!shownServiceType && (
                    <button
                        className="filed-button"
                        onClick={() => {
                            setShownServiceType(true); // Показываем AddServiceType
                            window.scrollTo(0, 0);
                        }}
                    >
                        Добавить тип услуги
                    </button>
                )}
            </div>

            <main className="main-block">
                {(shown || shownServiceType) && (
                    <div className="main-block-form">
                        {shown && <AddTariff setShown={setShown} requestFunction={addTariff} />}
                        {shownServiceType && (
                            <AddServiceType setShown={setShownServiceType} />
                        )}
                    </div>
                )}

                <div className="main-block-list">
                    {data.map((element, index) => {
                        return (
                            <TariffCard key={index} data={element} allData={data} />
                        );
                    })}
                </div>
            </main>
        </>
    );
}
