import { useEffect, useState } from "react";
import { fetchAllTariffs, addTariff, addService } from "../requests";

import TariffCard from "./ui/TariffCard";
import AddTariff from "./forms/AddTariff";
import AddService from "./forms/AddService"; // Новый компонент для добавления услуги

export default function Main() {
    const [data, setData] = useState([]); // Список тарифов с услугами
    const [showTariffForm, setShowTariffForm] = useState(false);
    const [showServiceForm, setShowServiceForm] = useState(false);

    useEffect(() => {
        fetchAllTariffs(setData);
    }, []);

    return (
        <>
            <div className="main-block-menu">
                {!showTariffForm && !showServiceForm && (
                    <>
                        <button
                            className="filed-button"
                            onClick={() => {
                                setShowTariffForm(true);
                                window.scrollTo(0, 0);
                            }}
                        >
                            Добавить тариф
                        </button>
                        <button
                            className="filed-button"
                            onClick={() => {
                                setShowServiceForm(true);
                                window.scrollTo(0, 0);
                            }}
                        >
                            Добавить услугу
                        </button>
                    </>
                )}
            </div>

            <main className="main-block">
                {showTariffForm && (
                    <div className="main-block-form">
                        <AddTariff setShown={setShowTariffForm} requestFunction={addTariff} />
                    </div>
                )}

                {showServiceForm && (
                    <div className="main-block-form">
                        <AddService
                            setShown={setShowServiceForm}
                            requestFunction={addService}
                            allTariffs={data} // Передача списка тарифов для выбора
                        />
                    </div>
                )}

                <div className="main-block-list">
                    {data.map((element, index) => (
                        <TariffCard key={index} data={element} allData={data} />
                    ))}
                </div>
            </main>
        </>
    );
}
