import { useEffect, useState } from "react";
import { addTariff, addService } from "../requests";

import TariffCard from "./ui/TariffCard";
import AddTariff from "./forms/AddTariff";
import AddService from "./forms/AddService";

export default function Main() {
    const [data, setData] = useState([]);
    const [shown, setShown] = useState(false);
    const [shown2, setShown2] = useState(false);

    useEffect(() => {
        fetchAllData(setData);
    }, []);

    return (
        <>
            <div className="main-block-menu">
                {!shown && (
                    <button
                        className="filed-button"
                        onClick={() => {
                            setShown(true);
                            window.scrollTo(0, 0);
                        }}
                    >
                        Добавить тариф
                    </button>
                )}
                {!shown2 && (
                    <button
                        className="filed-button"
                        onClick={() => {
                            setShown2(true);
                            window.scrollTo(0, 0);
                        }}
                    >
                        Добавить услугу
                    </button>
                )}
            </div>
            <main className="main-block">
                {(shown || shown2) && (
                    <div className="main-block-form">
                        {shown && (
                            <AddTariff setShown={setShown} requestFunction={addTariff} />
                        )}
                        {shown2 && (
                            <AddService setShown={setShown2} requestFunction={addService} />
                        )}
                    </div>
                )}

                <div className="main-block-list">
                    {data.map((element, index) => {
                        return (
                            <TariffCard key={index} data={element} allData={data} />
                        );
                    })}
                </div>
            </main>
        </>
    );
}
