import styled from 'styled-components';

const Logo = styled.img`
    width: auto;
    height: ${props => props.height || '50px'}; /* Значение по умолчанию 50px */
`;

export default function Header({ logoHeight }) {
    return (
        <header className="header-block">
            <div className="header-block-left-part">
                <Logo src="/images/logo.png" alt="Logo" height={logoHeight} />
            </div>
            <div className="header-block-right-part">
                <img
                    src="/images/avatar.png"
                    className="header-block-right-part-avatar"
                    alt="header-block-right-part-avatar"
                />
                <span className="header-block-right-part-name">
                    HR-специалист
                </span>
            </div>
        </header>
    );
}
