import { useEffect, useState } from "react";
import { fetchAllTariffs, addTariff } from "../requests";

import TariffCard from "./ui/TariffCard";
import AddTariff from "./forms/AddTariff";
import AddServiceType from "./forms/AddServiceType";

export default function Main() {
  const [data, setData] = useState([]); // Правильная инициализация
  const [shown, setShown] = useState(false);
  const [shownServiceType, setShownServiceType] = useState(false);

  useEffect(() => {
    const loadTariffs = async () => {
      try {
        const result = await fetchAllTariffs(); // Асинхронный вызов
        setData(result); // Обновление состояния
      } catch (error) {
        console.error("Ошибка при загрузке тарифов:", error);
      }
    };
    loadTariffs();
  }, []);

  return (
    <>
      <div className="main-block-menu">
        {!shown && (
          <button
            className="filed-button"
            onClick={() => {
              setShown(true);
              window.scrollTo(0, 0);
            }}
          >
            Добавить тариф
          </button>
        )}
        {!shownServiceType && (
          <button
            className="filed-button"
            onClick={() => {
              setShownServiceType(true);
              window.scrollTo(0, 0);
            }}
          >
            Добавить тип услуги
          </button>
        )}
      </div>

      <main className="main-block">
        {(shown || shownServiceType) && (
          <div className="main-block-form">
            {shown && <AddTariff setShown={setShown} requestFunction={addTariff} />}
            {shownServiceType && <AddServiceType setShown={setShownServiceType} />}
          </div>
        )}

        <div className="main-block-list">
          {data.map((element, index) => (
            <TariffCard key={index} data={element} allData={data} />
          ))}
        </div>
      </main>
    </>
  );
}
